mapper.select:
SELECT * 
FROM message
WHERE recv_name=#{recv_name} 
ORDER BY num desc LIMIT #{start}, #{perpage}

SELECT * 
FROM message
WHERE send_name=#{send_name}
ORDER BY num desc LIMIT #{start}, #{perpage}

mapper.update:
UPDATE message msg INNER JOIN user mem 
ON msg.recv_name=#{name} 
SET msg.read_chk=msg.read_chk+1 
WHERE msg.num=#{num}
